screen.applet:
from enigma import eSize, ePoint, gFont
c = len(self.list)
try:
    self['text'].instance.setFont(gFont('Regular', 30))
    self.title = self['text'].getText()
    
except Exception, ex:
    print str(ex)
maxlength = self["text"].getSize()[0]
self['text'].instance.setFont(gFont('Regular', 22))
isTitle = True
isDash = False
for i in self.list:
    checktext = str(i[0][0])
    self["text"].setText(checktext)
    mw = self["text"].getSize()[0]
    if (mw > maxlength):
        if str(i[0]) == "--":
            isDash = True 
        maxlength = mw
        isTitle = False
#print str(isDash) + "DASH"
#print str(isTitle ) + "TITLE"
if isDash == True:
    width = maxlength 
else:
    if isTitle == True:
        width = maxlength + 50
    else:
        width = maxlength + 50 
if (610 >width):
    width = 610
if (18 >c):
    self["list"].resize(eSize(width ,(c*50)))
    self.instance.resize(eSize(width ,(c*50)))
    self.instance.move(ePoint((1920-width )/2,((1080-(c*50))/2)+32))
else:
    self.instance.move(ePoint((1920-width )/2,((1080-900)/2)+32))
